namespace patika_if_else_tenary{internal class Program{static void Main(string[] args){SaatAralik();}public static void SaatAralik(){int time = DateTime.Now.Hour;if (time>=7&&time<12){Console.WriteLine("Günaydın");}else if (time >= 12 && time < 17){Console.WriteLine("Tünaydın");}else if (time >= 17 && time < 21){Console.WriteLine("İyi akşamlar");}else if (time >= 21 && time < 24){Console.WriteLine("İyi geceler");}else if (time >= 0 && time < 6){Console.WriteLine("İyi geceler");}}}}